package javaBasics;

public class IncrementalAndDecremental {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		

		//++
		//--
		
		//Post increment
		
		int i =1;
		int j= i++;
		
		System.out.println(i);
		System.out.println(j);
		
		//pre increment
		
		int k =1;
		int l=++k;
		System.out.println(k);
		System.out.println(l);
	}
		
	}

