package javaBasics;

public class StringConcatination {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		

		int a = 100;
		int b = 200;
		
		
		String x = "Eashwar";
		
		String y = "Reddy";
		
		
		System.out.println(a+b);
		
		System.out.println(x+y);
		
		
		System.out.println(a+b+x+y);
		
		System.out.println(x+y+a+b);
		
		System.out.println(x+y+(a+b));
		
		
	}

}
